(function(){
var querystring = require('querystring');
var https = require('https');

var host;
var serverAuthKey;
var contentType;
/*var username = 'JonBob';
var password = '*****';
var apiKey = '*****';
var sessionId = null;
var deckId = '68DC5A20-EE4F-11E2-A00C-0858C0D5C2ED';*/

module.exports.setHost = function(hostName){
  host = hostName;
};

module.exports.setServerAuthKey = function(serverKey){
  serverAuthKey = serverKey;
};

module.exports.setContentType = function(pContentType){
  contentType = pContentType;
};

module.exports.performRequest = function(endpoint, method, data, success) {
  var dataString = JSON.stringify(data);
  var headers = {};
  
  if (method == 'GET') {
    endpoint += '?' + querystring.stringify(data);
  }
  else {
    headers = {
      'Content-Type': contentType,
      'Content-Length': dataString.length,
      'Authorization' : serverAuthKey
    };
  }
  var options = {
    host: host,
    path: endpoint,
    method: method,
    headers: headers
  };

  var req = https.request(options, function(res) {
    res.setEncoding('utf-8');

    var responseString = '';

    res.on('data', function(data) {
      responseString += data;
    });

    res.on('end', function() {
      console.log(responseString);
      var responseObject = JSON.parse(responseString);
      success(responseObject);
    });
  });

  req.on("error", function(err){
    console.log("something went wrong: " + err);
  });

  req.write(dataString);
  req.end();
}
}());